    { key: 11, label: "keys()",function:'Returns a Array Iteration Object, containing the keys of the original array' },
    { key: 12, label: "pop()",function:'Removes the last element of an array, and returns that element' },
    { key: 13, label: "push()",function:'The push() method adds new items to the end of an array. eg. adds "1" to the end of the array' },
    { key: 14, label: "reduce()" },
    { key: 15, label: "reverse()",function:'Reverses the order of the elements in an array'},
    { key: 16, label: "shift()",function:'Removes the first element of an array, and returns that element' },
    { key: 17, label: "splice()" },
    { key: 18, label: "values()",function:'returns a new array iterator object that iterates the value of each item in the array.' },
    { key: 19, label: "sort()",function:"Sorts the elements of an array" },
    { key: 20, label: "slice()" },
  ];

