        break;
      case "forEach()":
        let text = "";
        newArray.forEach((item,index) => text+= index + ":" + item + "<br>")
        // updateArray(newArray);
        console.log(newArray);
        break;
      case "join()":
        console.log(newArray.join(" "));
        // updateArray(newArray);
        break;
      case "keys()":
        const keysArray = Array.from(newArray.keys());
        console.log("keys of array",keysArray);
        break;

      case "pop()":
        newArray.pop();
        // updateArray(newArray);
        console.log(newArray);
        break;
      case "push()":
        newArray.push("1");
        // console.log(newArray);
        break;
      case "shift()":
        newArray.shift();
