        break;
      case "splice()":
        let spliced = newArray.splice(1, 0, "feb");
        console.log(
          `insert at index 1, deletes 0 items from array,adds "feb" at index 1 ${spliced}`
        );
        // updateArray(newArray);
        break;
      case "every()":
        // newArray.every((currentValue) => typeof currentValue === "number");
        console.log(newArray.every((currentValue) => typeof currentValue === "number"));
        break;




      default:
        break;
    }
    // update the array after the switch statement
  return {
    array:array,
    updatedArray:newArray
  }
  };
  return (
    <>
      <Typography
        variant="h6"
        gutterBottom
        sx={{
          margin: "1rem ",
          color: "#1565c0",
          textTransform: "capitalize",
          fontWeight: "bold",
          letterSpacing: "2px",
        }}
      >
        select an Activity to perform on your Array.
      </Typography>
      <Paper
        elevation={0}
        sx={{
          display: "flex",
          justifyContent: "center",
          flexWrap: "wrap",
          listStyle: "none",
          p: "0.5",
          m: "0",
        }}
      >
        {activities.map((activity) => {
          return (
            <ListItem key={activity.key} sx={{ padding: "0.5rem" }}>
              <Chip
                onClick={() => handleActivityClick(activity)}
                // variant={selectedActivity === activity ? "filled" : "outlined"}
                // color={selectedActivity === activity ? "primary.main" : "default"}
                color="primary"
                variant="outlined"
                label={activity.label}
                className={classes.chip}
              />
            </ListItem>
          );
        })}
      </Paper>
      <ResultantArray
        array={array}
        updatedArray={selectedActivity ? applyActivity(selectedActivity).updatedArray : array}
        selectedActivity={selectedActivity}
      />
    </>
  );
}
export default ListOfActivities;
