import Accordion from "@mui/material/Accordion";
import AccordionSummary from "@mui/material/AccordionSummary";
import AccordionDetails from "@mui/material/AccordionDetails";
import Box from "@mui/material/Box";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import InputAdornment from "@mui/material/InputAdornment";
import { useState } from "react";
import ListOfActivities from "./ListOfActivities";

function InputArray() {
  // Define state variables using useState hook
  const [array, setArray] = useState(); // To store the user-input array as an array of items
  const [arrayAsString, setArrayAsString] = useState();// To store the user-input array as a string
  const [showActivities, setShowActivities] = useState(false);

  const handleChange = (e) => {
    setArrayAsString(e.target.value);
  };

  const handleAdd = () => {
    const newArray = arrayAsString.split(",").map((item) => item.trim());
    console.log(`input array = ${newArray}`);
    setArray(newArray);

    setArrayAsString("");
    setShowActivities(true);
  };

  return (
    <>
      <Box sx={{ bgcolor: "white" }}>
        <Accordion>
          <AccordionSummary>
            <Button
              variant="contained"
              size="large"
              sx={{ fontWeight: "bold" }}
            >
              Take My Own Array
            </Button>
          </AccordionSummary>
          <AccordionDetails>
            <TextField
              fullWidth
              id="fullWidth"
              label="Text field with button"
              placeholder="Enter Comma Seperated Array Items"
              multiline
              variant="outlined"
              value={arrayAsString}
              onChange={handleChange}
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <Button
                      variant="contained"
                      onClick={handleAdd}
                      color="success"
                      sx={{ fontSize: "1rem", fontWeight: "bold" }}
                    >
                      Add
                    </Button>
                  </InputAdornment>
                ),
              }}
            />
          </AccordionDetails>
        </Accordion>
      </Box>
      <ListOfActivities
        array={array}
        updatedArray={setArray}
        showActivities={showActivities}
      />
    </>
  );
}
export default InputArray;
