import Accordion from "@mui/material/Accordion";
import AccordionSummary from "@mui/material/AccordionSummary";
import AccordionDetails from "@mui/material/AccordionDetails";
import Box from '@mui/material/Box'; 
import  Button  from "@mui/material/Button";
import TextField from '@mui/material/TextField';
import InputAdornment from '@mui/material/InputAdornment';
import { useState } from "react";

function InputArray() {
  const intialArray = []
  const [arrayAsString, setArrayAsString] = useState(intialArray.join(', '))

  const handleChange = (e) => {
    setArrayAsString(e.target.value)
}

  const handleClick = () => {
    const newArray = arrayAsString.split(',')
    console.log(newArray);
  }

  return (
    <>
      <Box sx={{ bgcolor: "white" }}>
        <Accordion>
          <AccordionSummary>
            <Button variant="contained">Take My Own Array</Button>
          </AccordionSummary>
          <AccordionDetails>

          <TextField
          fullWidth
          id="fullWidth"
          label="Text field with button"
          placeholder="Enter Array Items .."
          multiline
          variant="outlined"
          value={arrayAsString}
          onChange={handleChange}
          InputProps={{
           endAdornment: (
            <InputAdornment position="end">
            <Button 
            variant="contained"
            onClick={handleClick}
            >
              Add
            </Button>
            </InputAdornment>
           )
          }}
        />
          </AccordionDetails>
        </Accordion>
      </Box>
    </>
  );
}
export default InputArray;
