import { styled } from "@mui/material/styles";
import Chip from "@mui/material/Chip";
import Paper from "@mui/material/Paper";
import Typography from "@mui/material/Typography";
import { useState } from "react";

const ListItem = styled("li")(({ theme }) => ({
  margin: theme.spacing(0.5),
}));

function ListOfActivities({array,updateArray}) {
  const [selectedActivity,setSelectedActivity] = useState(null)

  const activities = [
    { key: 0, label: "entries()" },
    { key: 1, label: "every()" },
    { key: 2, label: "filter()" },
    { key: 3, label: "find()" },
    { key: 4, label: "findIndex()" },
    { key: 5, label: "findLast()" },
    { key: 6, label: "flat()" },
    { key: 7, label: "forEach()" },
    { key: 8, label: "icludes()" },
    { key: 9, label: "indexOf()" },
    { key: 10,label: "join()" },
    { key: 11,label: "keys()" },
    { key: 12,label: "pop()" },
    { key: 13,label: "push()" },
    { key: 14,label: "reduce()" },
    { key: 15,label: "reverse()" },
    { key: 16,label: "shift()" },
    { key: 17,label: "unshift()" },
    { key: 18,label: "values()" },
    { key: 19,label: "sort()" },
    { key: 20,label: "slice()" },
  ];

  const handleActivityClick = (activity) => {
    setSelectedActivity(activity);
    applyActivity(activity);
  };

  const applyActivity = (activity) => {
    // Update the array using updateArray function
    //  create a new array instance before modifying
    const newArray = [...array];
    switch (activity.label) {
      case "sort()":
        newArray.sort();
         updateArray(newArray);
        console.log(newArray);
        break;
      case "filter()":
        const result = newArray.filter((word) => word.length > 6);
        updateArray(result);
        console.log(result);
        break;
      case "find()":



      default:
        break;  

    }
  }
  return (
    <>
      <Typography variant='h6' gutterBottom>
        Select an activity to perform on your Array.
      </Typography>
      <Paper
        sx={{
          display: "flex",
          justifyContent: "center",
          flexWrap: "wrap",
          listStyle: "none",
          p: "0.5",
          m: "0",
        }}
      >
        {activities.map((activity) => {
          return (
            <ListItem key={activity.key}>
              <Chip
                onClick={() => handleActivityClick(activity)}
                variant={selectedActivity === activity ? "filled" : "outlined"}
                color={selectedActivity === activity ? "primary" : "default"}
                label={activity.label}
              />
            </ListItem>
          );
        })}
      </Paper>
    </>
  );
}
export default ListOfActivities;
