import Accordion from "@mui/material/Accordion";
import AccordionSummary from "@mui/material/AccordionSummary";
import AccordionDetails from "@mui/material/AccordionDetails";
import Box from '@mui/material/Box'; 
import Button  from "@mui/material/Button";
import TextField from '@mui/material/TextField';
import InputAdornment from '@mui/material/InputAdornment';
import { useState } from "react";
import ListOfActivities from "./ListOfActivities";

function InputArray() {
  const [array,setArray] = useState([]);
  const [arrayAsString, setArrayAsString] = useState();

  const handleChange = (e) => {
    setArrayAsString(e.target.value)
    console.log(setArrayAsString);
}

  const  handleAdd = () => {
    const newArray = arrayAsString.split(',').map((item) => item.trim());

    console.log(`input array = ${newArray}`);
     console.log(setArray(newArray));
    setArrayAsString("");
  }

  return (
    <>
      <Box sx={{ bgcolor: "white" }}>
        <Accordion>
          <AccordionSummary>
            <Button variant="contained">Take My Own Array</Button>
          </AccordionSummary>
          <AccordionDetails>

          <TextField
          fullWidth
          id="fullWidth"
          label="Text field with button"
          placeholder="Enter Array Items in Square brackets.."
          multiline
          variant="outlined"
          value={arrayAsString}
          onChange={handleChange}
          InputProps={{
           endAdornment: (
            <InputAdornment position="end">
            <Button 
            variant="contained"
            onClick={handleAdd}
            >
              Add
            </Button>
            </InputAdornment>
           )
          }}
        />
          </AccordionDetails>
        </Accordion>
      </Box>
      <ListOfActivities array = {array} updateArray = {setArray}/>

    </>
  );
}
export default InputArray;
