import { styled } from "@mui/material/styles";
import Chip from "@mui/material/Chip";
import Paper from "@mui/material/Paper";
import Typography from "@mui/material/Typography";
import { useState } from "react";

const ListItem = styled("li")(({ theme }) => ({
  margin: theme.spacing(0.5),
}));

function ListOfActivities({ array, updateArray }) {
  const [selectedActivity, setSelectedActivity] = useState(null);

  const activities = [
    { key: 0, label: "toString()" },
    { key: 1, label: "every()" },
    { key: 2, label: "filter()" },
    { key: 3, label: "reduceRight()" },
    { key: 4, label: "findIndex()" },
    { key: 5, label: "findLast()" },
    { key: 6, label: "flat()" },
    { key: 7, label: "forEach()" },
    { key: 8, label: "icludes()" },
    { key: 9, label: "indexOf()" },
    { key: 10, label: "join()" },
    { key: 11, label: "keys()" },
    { key: 12, label: "pop()" },
    { key: 13, label: "push()" },
    { key: 14, label: "reduce()" },
    { key: 15, label: "reverse()" },
    { key: 16, label: "shift()" },
    { key: 17, label: "splice()" },
    { key: 18, label: "values()" },
    { key: 19, label: "sort()" },
    { key: 20, label: "slice()" },
  ];

  const handleActivityClick = (activity) => {
    setSelectedActivity(activity);
    // applyActivity(activity);
  };

  const applyActivity = (activity) => {
    // Update the array using updateArray function
    //  create a new array instance before modifying
    const newArray = [...array];

    switch (activity.label) {
      case "sort()":
        newArray.sort();
        // updateArray(newArray);
        console.log(newArray);
        break;
      case "filter()":
        const filteredArr = newArray.filter((item) => item > 6);
        console.log(newArray);
        updateArray(filteredArr);
        break;
      case "findIndex()":
        const index = newArray.findIndex((item) => item > 18);
        console.log(index);
        break;

          // updateArray(newArray);


      case "reverse()":
        let reversedArr = newArray.reverse();
        console.log(reversedArr);
        updateArray(newArray);
        break;
      case "flat()":
        let flat = newArray.flat();
        console.log(flat);
        // updateArray(newArray);
        break;
      case "forEach()":
        newArray.forEach((el) => console.log(el));
        // updateArray(newArray);
        break;
      case "join()":
        console.log(newArray.join(" "));
        // updateArray(newArray);
        break;
      case "keys()":
        const keysArray = Array.from(newArray.keys());
        updateArray(keysArray);
        break;

      case "pop()":
        newArray.pop();
        // updateArray(newArray);
        console.log(newArray);
        break;
      case "push()":
        newArray.push("1");
        // console.log(newArray);
        break;
      case "shift()":
        newArray.shift();
        updateArray(newArray);
        // console.log(newArray);
        break;
      case "values()":
        const valuesArray = Array.from(newArray.values());
        updateArray(valuesArray);
        break;


      case "reduceRight()":
        const reduced = newArray.reduceRight((accumulator, currentValue) =>
          accumulator.concat(currentValue),[]
        );
        console.log(reduced);
        // updateArray(newArray);
        break;
      case "slice()":
        console.log(`slicing starts from index 2 ${newArray.slice(2)}`);
        // updateArray(newArray);
        break;
      case "splice()":
        let spliced = newArray.splice(1, 0, "feb");
        console.log(
          `insert at index 1, deletes 0 items from array,adds "feb" at index 1 ${spliced}`
        );
        // updateArray(newArray);
        break;

      default:
        break;
    }
    // update the array after the switch statement
    updateArray(newArray);
  };
  return (
    <>
      <Typography variant="h6" gutterBottom>
        Select an activity to perform on your Array.
      </Typography>
      <Paper
        sx={{
          display: "flex",
          justifyContent: "center",
          flexWrap: "wrap",
          listStyle: "none",
          p: "0.5",
          m: "0",
        }}
      >
        {activities.map((activity) => {
          return (
            <ListItem key={activity.key}>
              <Chip
                onClick={() => handleActivityClick(activity)}
                variant={selectedActivity === activity ? "filled" : "outlined"}
                color={selectedActivity === activity ? "primary" : "default"}
                label={activity.label}
              />
            </ListItem>
          );
        })}
      </Paper>
    </>
  );
}
export default ListOfActivities;
